mapper.insert:
INSERT INTO TBL_MENU (
	CODE
	, CODENAME
	, SORT_NUM
	, COMMENT
	, REG_ID
	, REG_DT
) VALUES (
	#{code}
	, #{codename}
	, #{sort_num}
	, #{comment}
	, #{reg_id}
	, now()
)

mapper.update:
UPDATE TBL_MENU SET
	CODENAME = #{codename}
	, COMMENT = #{comment}
	, SORT_NUM = #{sort_num}
WHERE CODE = #{code}
